package org.example.binarytree;import org.example.util.TreeNode;import java.util.ArrayList;import java.util.List;import java.util.Map;public class SmallestSubTreeWithDeepestNodes {    public static void main(String[] args) {    }    private void buildMap(TreeNode node, Map<Integer, List<TreeNode>> map) {        if(node == null)            return;        int depth=0;        map.computeIfAbsent(depth, k -> new ArrayList<TreeNode>()).add(node);    }}