package org.example.dp;import java.util.Arrays;import java.util.Map;public class Dimension2 {    public static void main(String[] args) {        Dimension2 d2 = new Dimension2();        System.out.println(d2.LCS("abcde", "ace"));        System.out.println(d2.editDistance());    }    public int LCS(String t1, String t2) {        int m = t1.length();        int n = t2.length();        int[][] memo = new int[m][n];        for(int[] row: memo){            Arrays.fill(row, -1);        }        return lcsDP(t1, t2, m-1, n-1, memo);    }    private int lcsDP(String s1, String s2, int i, int j, int[][] memo) {        if(i<0 || j <0) return 0;        if(memo[i][j] != -1) return memo[i][j];        if(s1.charAt(i) == s2.charAt(j)) {            memo[i][j] = 1 + lcsDP(s1, s2, i-1, j-1, memo);        } else {            int cond1 = lcsDP(s1, s2, i-1, j, memo);            int cond2 = lcsDP(s1, s2, i, j-1, memo);            memo[i][j] = Math.max(cond1, cond2);        }        return memo[i][j];    }    public int editDistance() {        String w1 = "horse";        String w2 = "ros";        int m = w1.length();        int n = w2.length();        int[][] memo = new int[m][n];        for(int[] row:memo) Arrays.fill(row, -1);        return edDP(m-1, n-1, w1, w2, memo);    }    private int edDP(int i, int j, String s1, String s2, int[][] memo) {        if(i<0) return j+1;        if(j<0) return i+1;        if(memo[i][j] != -1) return memo[i][j];        System.out.println("++++++ "+i+ "++++ "+j+" +++++++++\n");        print2DArray(memo);        System.out.println("\n+++++++++++++++++++");        if(s1.charAt(i) == s2.charAt(j)) {            memo[i][j] = edDP(i-1, j-1, s1, s2, memo);        } else {            int insert = edDP(i, j-1, s1, s2, memo);            int delete = edDP(i-1, j, s1, s2, memo);            int replace = edDP(i-1, j-1, s1, s2, memo);            memo[i][j] = 1+ Math.min(insert, Math.min(delete, replace));        }        return memo[i][j];    }    void print2DArray(int[][] arr) {        for(int i = 0;i<arr.length;i++) {            for(int j=0;j<arr[i].length;j++) {                System.out.print(" "+arr[i][j]);            }            System.out.println();        }    }}