package org.example.greedy;import org.example.Pair;import java.util.ArrayList;import java.util.Comparator;import java.util.List;import static java.lang.StringTemplate.STR;public class ActivitySelection {    public static void main(String[] args) {        ActivitySelection activity = new ActivitySelection();        System.out.println(STR."No of activities: \{activity.calculate()}");    }    int calculate() {        int[] start = {1, 3, 0, 5, 8, 5};        int[] end =   {2, 4, 6, 7, 9, 9};        List<Pair> pairList = new ArrayList<>();        for(int i = 0 ; i< start.length;i++) {            pairList.add(new Pair(start[i], end[i]));        }        pairList.sort(Comparator.comparingInt(c -> c.end));        int count=0;        int last_end = 0;        for(Pair pair: pairList) {            if(pair.start >= last_end) {                count++;                last_end = pair.end;            }        }        return count;    }}