package org.example.string;import java.util.Arrays;public class MinWindowSubstring {    public static void main(String[] args) {        MinWindowSubstring win = new MinWindowSubstring();        String s = "ADOBECODEBANC";        String t = "ABC";        String ans = win.minSubstring(s,t);        System.out.println("Ans : "+ ans);    }    public String minSubstring(String source, String target) {        if(source.length() < target.length()) {            return "";        }        if(source.length() == target.length()) {            if(source.equals(target))                return target;        }        int L=0, R=0;        int curLen=0, minLen= Integer.MAX_VALUE;        int counter=0;        char[] s_arr = source.toCharArray();        char[] t_arr = target.toCharArray();        String ans = "";        int[] arr = new int[128];        for(int i=0;i<target.length();i++) {            arr[t_arr[i]]++;        }        while(R < source.length()) {            int rChar = s_arr[R];            if(-- arr[rChar] >= 0) {                counter++;            }            while(counter == t_arr.length) {                curLen = R-L+1;                if(minLen > curLen) {                    minLen = curLen;                    ans = source.substring(L, R+1);                }                char lChar = s_arr[L];                if(++arr[lChar] > 0) {                    counter--;                }                L++;            }            R++;        }        return ans;    }}