package org.example.util;public class TreeNode {    public int val;    public TreeNode left;    public TreeNode right;    int index;    public TreeNode(int val) {        this.val = val;    }    public TreeNode(int val, int index) {        this.val = val;        this.index = index;    }    TreeNode(int val, TreeNode left, TreeNode right) {          this.val = val;          this.left = left;          this.right = right;    }    TreeNode() {}    @Override    public String toString() {        return STR."val=\{val}, index=\{index}";    }}